import 'package:flutter/material.dart';import 'package:soowgood/common/resources/my_colors.dart';import 'package:webviewx/webviewx.dart';import '../dialog/custom_progress_dialog.dart';class PrivacyPolicy extends StatefulWidget {  String data;  PrivacyPolicy(this.data);  @override  State<PrivacyPolicy> createState() => _PrivacyPolicyState();}class _PrivacyPolicyState extends State<PrivacyPolicy> {  late WebViewXController webviewController;  CustomProgressDialog progressDialog = CustomProgressDialog();    @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: MyColor.themeTealBlue,        leading: IconButton(          onPressed: (){            Navigator.pop(context);          },          icon: Icon(Icons.arrow_back,color: Colors.white,),        ),      ),       body: WebViewX(         height: MediaQuery.of(context).size.height,         width: MediaQuery.of(context).size.width,         initialContent: widget.data.toString(),         initialSourceType: SourceType.html,         onPageStarted: (src){         },         onPageFinished: (src){           progressDialog.close();         },         onWebViewCreated: (controller){           webviewController = controller;           progressDialog.showProgressDialog();         },       )    );  }}